$(function(){

	const 	global_var = {
				global_modal: '_global_modal'
			}

	// Preventing Default Behaviour For Hashed Link
	$('a[href="#"]').click(function(event){ event.preventDefault(); }); 

	// Initialize Bootstrap Tooltip;
	function init_tooltip()
	{ 
		$('[data-toggle="tooltip"], [data-plugin="tooltip"]').tooltip({'container' : 'body', 'html' : true});
	}
	// //////////////////////////////////////////////////////////////////////////////////////////////////////

	init_tooltip();

	// Global Modal Handler
	$('[data-toggle="modal"]').click(function(event){
		event.preventDefault();

		var modal_template = `
			<div class="modal-dialog">
				<div class="modal-content">
					<div class="modal-header">
						<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>
						<h4 class="modal-title">Modal title</h4>
					</div>
					<div class="modal-body">
						<h4>Hi</h4>
					</div>
					<div class="modal-footer">
						<button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
						<button type="button" class="btn btn-primary">Save changes</button>
					</div>
				</div>
			</div>
		`;

		$(`#${global_var.global_modal}`).html(modal_template);

		$(`#${global_var.global_modal}`).modal('show');

	});


});